package Prac.com;

public class parse6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int row=5;
		for(int i=1;i<=row;i++)
		{
			for(int j=1;j<=row-i;j++)
			{
				System.out.print("-"+" ");
			}
			for(int j=1;j<=5;j++)
			{
				System.out.print("*"+" ");
			}
			System.out.println();

	}

}
}
